import React,{ useState, useRef } from 'react';
import{
    Image, 
    SafeAreaView, 
    Text, 
    View,
    ScrollView,
    TouchableOpacity,
    FlatList,
    Animated,
    StyleSheet
} from 'react-native';

import {icons,COLORS,SIZES} from '../constants';
import {categories} from '../localdata/data';

const Categories = () =>{

    const categoryListHeightAnimationValue = useRef(new Animated.Value(115)).current;
    const [selectedCategory, setSelectedCategory] = useState(null)
    const [showMoreToggle, setShowMoreToggle] = useState(false)

    const renderItem = ({ item }) => (
        <TouchableOpacity
            onPress={() => setSelectedCategory(item)}
            style={{
                flex: 1,
                flexDirection: 'row',
                margin: 5,
                paddingVertical: SIZES.radius,
                paddingHorizontal: SIZES.padding,
                borderRadius: 5,
                backgroundColor: COLORS.white,
                ...styles.shadow
            }}
        >
            <Image
                source={item.icon}
                style={{
                    width: 30,
                    height: 30
                }}
            />
            <Text style={{ marginLeft: SIZES.base, color: COLORS.primary,alignSelf:'center',
        justifyContent:'center',fontSize:16 }}>{item.name}</Text>
        </TouchableOpacity>
    )

    return (
        <View style={{ paddingHorizontal: SIZES.padding - 5 }}>
            <Animated.View style={{ height: categoryListHeightAnimationValue }}>
                <FlatList
                    data={categories}
                    renderItem={renderItem}
                    keyExtractor={item => `${item.id}`}
                    numColumns={2}
                />
            </Animated.View>

            <TouchableOpacity
                style={{
                    flexDirection: 'row',
                    marginVertical: SIZES.base,
                    justifyContent: 'center'
                }}
                onPress={() => {
                    if (showMoreToggle) {
                        Animated.timing(categoryListHeightAnimationValue, {
                            toValue: 115,
                            duration: 500,
                            useNativeDriver: false
                        }).start()
                    } else {
                        Animated.timing(categoryListHeightAnimationValue, {
                            toValue: 172.5,
                            duration: 500,
                            useNativeDriver: false
                        }).start()
                    }

                    setShowMoreToggle(!showMoreToggle)
                }}
            >
                <Text>{showMoreToggle ? "LESS" : "MORE"}</Text>
                <Image
                    source={showMoreToggle ? icons.up_arrow : icons.down_arrow}
                    style={{ marginLeft: 5, width: 15, height: 15, alignSelf: 'center' }}
                />
            </TouchableOpacity>
        </View>
    )
}
const styles = StyleSheet.create({
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 2,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 3,
    }
})
export default Categories;
