import React,{useState,useEffect,useContext} from 'react';
import { 
    View,
    StyleSheet,
    Text,
    FlatList,
    TouchableOpacity,
    ActivityIndicator,
} from 'react-native';
import questionApi from '../api/baseApi';
import {COLORS, SIZES} from '../constants';
import { MaterialIcons } from '@expo/vector-icons';
const ModelTestScreen = ({route})=>{
    const id = route.params.tropicsId
    const [questions,setQuestions] = useState([]);
    const [isLoading,setIsloading] = useState(true);
    const [score,setScore] =  useState(0);
    const [selected,setSelected] = useState([]);

    const [currentSelected,setCurrentSelected] = useState('');
    const [isSelected,setIsSelected] = useState(false);
    const [color,setColor] = useState('gray')

    const loadQuestions = async()=>{
        const {data} = await questionApi.get(`/get-questions/${id}`);
        setQuestions(data);
        setIsloading(false);

    }

    const handleAnswer = (qid,option)=>{

        setSelected([...selected,{
            qid:qid,
            option:option.qop
        }]);



        // if(item.isCorrect){
        //     setScore(score+1);
        // }

    }





    useEffect(()=>{
        loadQuestions();
    },[]);



    const renderItem = ({item})=>{
        return(
            <View>
            <View style={{
                flex: 1,
                flexDirection: 'column',
                margin: 5,
                paddingVertical: 15,
                paddingHorizontal: 18,
                borderRadius: 5,
                backgroundColor: COLORS.white,
                ...styles.shadow

            }}>
                <View>
                </View>
                <Text style={{fontWeight:'bold',fontSize:15,color:COLORS.black}}>{item.question}</Text>
                <View>

                    <TouchableOpacity
                        onPress={()=>handleAnswer(item._id,item.options[0])}
                         style={{flexDirection:'row',paddingVertical:5,alignItems:'center'}}>
                            <View style={{
                                backgroundColor:selected.map((itm)=>{
                                    itm.qid == item._id && itm.option == item.options[0].qop?"red":'blue'
                                })?color:'gray',
                                width:27,
                                height:27,
                                borderRadius:40,
                                marginRight:6,
                                justifyContent:'center'
                                }}>
                            
                            <Text style={{
                                alignSelf:'center',
                                color:'white',
                            }}>{item.options[0].qop}</Text>
                            </View>
                            <Text style={{fontSize:15}}>{item.options[0].option}</Text>
                    </TouchableOpacity>
                    <TouchableOpacity
                        onPress={()=>handleAnswer(item._id,item.options[1])}
                         style={{flexDirection:'row',paddingVertical:5,alignItems:'center'}}>
                            <View style={{
                                backgroundColor:selected.map((itm)=>{
                                    itm.qid == item._id && itm.option == item.options[1].qop?"red":'blue'
                                })?color:'gray',
                                width:27,
                                height:27,
                                borderRadius:40,
                                marginRight:6,
                                justifyContent:'center'
                                }}>
                            
                            <Text style={{
                                alignSelf:'center',
                                color:'white',
                            }}>{item.options[1].qop}</Text>
                            </View>
                            <Text style={{fontSize:15}}>{item.options[1].option}</Text>
                    </TouchableOpacity>
                    
                {/* <FlatList 
                data={item.options}
                keyExtractor={item=>item._id}
                renderItem={({item})=>{
                    return(
                        <TouchableOpacity
                        onPress={()=>handleAnswer(item)}
                         style={{flexDirection:'row',paddingVertical:5,alignItems:'center'}}>
                            <View style={{
                                backgroundColor:selected.find(id=>id == item._id) && isSelected ?COLORS.darkgreen:'gray',
                                width:27,
                                height:27,
                                borderRadius:40,
                                marginRight:6,
                                justifyContent:'center'
                                }}>
                            
                            <Text style={{
                                alignSelf:'center',
                                color:'white',
                            }}>{item.qop}</Text>
                            </View>
                            <Text style={{fontSize:15}}>{item.option}</Text>
                        </TouchableOpacity>
                    )
                }}
                /> */}
            </View>
            <Text style={{color:COLORS.darkgray,fontSize:13}}>{item.describe?item.describe:''}</Text>
            </View>
                
        </View>
        )

    }



    return(
        <View style={styles.container}>
            <Text>{score}</Text>
            {isLoading?<ActivityIndicator size="large" color={COLORS.blue} />:
            <FlatList
            data={questions}
            keyExtractor={item=>item._id}
            renderItem={renderItem}
            numColumns={1}
            />
            }
        </View>
    )
}
const styles = StyleSheet.create({
    container:{
        flex:1,
        backgroundColor:COLORS.lightGray2,
        paddingVertical:5
    },
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 2,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 3,
    }
})
export default ModelTestScreen;
