import React,{useState} from 'react';
import { 
    View,
    Text,
    StyleSheet,
    ScrollView,
    TouchableOpacity
 } from 'react-native';
import {bankCategories} from '../localdata/data';
import {COLORS,SIZES} from '../constants';
const BankCategoryScreen = ()=>{
    const [isLoading,setIsloading] = useState(true);

    return(
        <View style={styles.container}>
            <View style={{paddingVertical:6,backgroundColor:COLORS.darkgray}}>
            <Text style={{fontSize:14,color:COLORS.white,fontWeight:'bold',letterSpacing:6,alignSelf:'center'}}>PRELIMINARY</Text>
            </View>
        <ScrollView
        showsVerticalScrollIndicator={false}
        contentContainerStyle={{
            justifyContent:'flex-start',
            flexDirection:'column',
        }}
        >
            <TouchableOpacity style={styles.categoryStyle}>
                <View>
                <Text>{bankCategories[0].name}</Text>
                </View>
            </TouchableOpacity>
            <TouchableOpacity style={styles.categoryStyle}>
                <View>
                <Text>{bankCategories[1].name}</Text>
                </View>
            </TouchableOpacity>
            <TouchableOpacity style={styles.categoryStyle}>
                <View>
                <Text>{bankCategories[2].name}</Text>
                </View>
            </TouchableOpacity>
        </ScrollView>
        </View>
    )
}
const styles = StyleSheet.create({
    container:{
        flex:1,
        backgroundColor:COLORS.lightGray,
        paddingVertical:5
    },
    categoryStyle:{
        flexDirection: 'column',
        margin: 5,
        paddingVertical: SIZES.radius,
        paddingHorizontal: SIZES.padding,
        borderRadius: 5,
        backgroundColor: COLORS.white,
        justifyContent:'center',
        shadowColor: "#000",
        shadowOffset: {
            width: 2,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 3,
    }
})
export default BankCategoryScreen;
