import React,{useState} from 'react';
import { 
    View,
    Text,
    StyleSheet,
    ScrollView,
    TouchableOpacity
 } from 'react-native';
import {bankCategories} from '../localdata/data';
import {COLORS,SIZES} from '../constants';
const BankCategoryScreen = ()=>{
    const [isLoading,setIsloading] = useState(true);

    const renderItem = ({item})=>{
        return(
            <View>
                <TouchableOpacity>
                    <Text>{item.name}</Text>
                </TouchableOpacity>
            </View>
        )
    }

    return(
        <View style={styles.container}>
        <View style={{paddingHorizontal:SIZES.padding}}>
        <Text style={{fontSize:SIZES.h2,fontWeight:'bold',letterSpacing:1}}>Priliminary</Text>
        </View>
        <ScrollView
        contentContainerStyle={{
            justifyContent:'flex-start',
            flexDirection:'column'
        }}
        >
            <TouchableOpacity style={{
                    flexDirection: 'column',
                    margin: 5,
                    paddingVertical: SIZES.radius,
                    paddingHorizontal: SIZES.padding,
                    borderRadius: 5,
                    backgroundColor: COLORS.white,
                    justifyContent:'center',
                    ...styles.shadow
            }}>
                <View>
                <Text>{bankCategories[0].name}</Text>
                </View>
            </TouchableOpacity>
        </ScrollView>
        <View style={{paddingHorizontal:SIZES.padding}}>
        <Text style={{fontSize:SIZES.h2,fontWeight:'bold',letterSpacing:1}}>Written</Text>
        </View>
        </View>
    )
}
const styles = StyleSheet.create({
    container:{
        flex:1,
        backgroundColor:COLORS.lightGray,
        paddingVertical:5
    },
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 2,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 3,
    }
})
export default BankCategoryScreen;
