import React,{ useState, useRef,useEffect } from 'react';
import 
{   
    Image, 
    SafeAreaView, 
    Text, 
    View,
    TouchableOpacity,
    FlatList,
    Animated,
    StyleSheet,
    ActivityIndicator
} from 'react-native';
//import icons
import { Ionicons } from '@expo/vector-icons';
import { Feather } from '@expo/vector-icons';
import { FontAwesome5 } from '@expo/vector-icons';

import {icons,COLORS,SIZES} from '../constants';
import { AntDesign } from '@expo/vector-icons';
import {categories} from '../localdata/data';
//import api
import jobNewsApi from '../api/jobnews';

const HomeScreen = ({ navigation }) =>{


    const categoryListHeightAnimationValue = useRef(new Animated.Value(200)).current;

    const [showMoreToggle, setShowMoreToggle] = useState(false)
    const [isLoadingCircular,setIsLoadingCircular] = useState(true);
    const [jobNews,setJobNews] = useState([]);


    const loadJobNews = async()=>{
        const jobNewsData = await jobNewsApi.get('/get-all-circular');
        setJobNews(jobNewsData);
        setIsLoadingCircular(false);
    }

    useEffect(() => {
        loadJobNews();
    }, [0])



    //render header component
    const renderHeader=()=>{
        return(
            <View style={{
                flexDirection:'row',
                justifyContent:'space-between',
                paddingHorizontal:8,
                paddingVertical:25,
                alignItems:'center',
                

                }}>
                <View style={{alignSelf:'center',justifyContent:'center'}}>
                <Ionicons 
                style={{paddingLeft:0,alignSelf:'center'}}
                name="ios-menu"
                 size={40}
                 color={COLORS.yellow}
                  onPress={()=>{navigation.openDrawer()}}
                   />
                </View>
                <View>
                <Image 
                source={icons.biome}
                style={{
                    width:145,
                    height:65,
                    resizeMode:'contain',
                    alignSelf:'center'
                }}
                />
                </View>
                
                <View>
                <Feather name="more-vertical" size={24} color={COLORS.yellow} />
                </View>
            </View>
        )
    }

    //render News component
    const renderNews = ()=>{
        return(
        <View style={{paddingHorizontal:8,paddingBottom:15}}>
            <View style={{
                flexDirection:'row',
                paddingHorizontal:5,
                alignItems:'center',
                paddingBottom:10
                }}>
            <AntDesign name="calendar" size={28} color={COLORS.yellow} />
            <Text style={{
                fontWeight:'bold',
                fontSize:22,
                marginLeft:5,
                color:COLORS.black
                }}>Job Circular</Text>
            </View>
            {isLoadingCircular?<ActivityIndicator size="large" color={COLORS.yellow} /> :
            <FlatList
            data={jobNews}
            keyExtractor={item=>`${item.id}`}
            renderItem={({item})=>{
                return(
                <TouchableOpacity
                style={{
                    flex: 1,
                    flexDirection: 'column',
                    margin: 5,
                    paddingVertical: SIZES.radius,
                    paddingHorizontal: 18,
                    borderRadius: 5,
                    backgroundColor: COLORS.white,
                    ...styles.shadow
                }}
            >
                <View>
                    <Text
                    style={{fontWeight:'bold',fontSize:18,color:COLORS.darkgreen}}
                    >{item.title}</Text>
                </View>
                <View style={{flexDirection:'row',alignItems:'center'}}>
                    <Text style={{
                        color:COLORS.blue,
                        fontSize:15,
                        fontWeight:'600'
                    }}>Company : {item.company}</Text>
                    <AntDesign style={{marginLeft:5}} name="doubleright" size={13} color={COLORS.yellow} />
                    <Text style={{fontSize:13,color:COLORS.yellow}}>Details</Text>
                </View>
                <View style={{flexDirection:'row',justifyContent:'space-between'}}>
                    <Text style={{fontSize:12,color:COLORS.gray}}>Publish: 19/1/21</Text>
                    <Text style={{fontSize:12,color:COLORS.red}}>Dead Line: 25/2/21</Text>
                </View>
            </TouchableOpacity>
            )
            }}
            horizontal
            showsHorizontalScrollIndicator={false}
            />}

        </View>
        )
    }
   
    const renderItem = ({ item }) => (
        <TouchableOpacity
            onPress={() =>navigation.push('BCS')}
            style={{
                flex: 1,
                flexDirection: 'column',
                margin: 5,
                paddingVertical: SIZES.radius,
                paddingHorizontal: SIZES.padding,
                borderRadius: 5,
                backgroundColor: COLORS.white,
                justifyContent:'center',
                alignItems:'center',
                ...styles.shadow
            }}
        >
            <Image
                source={item.icon}
                style={{
                    width: 40,
                    height: 40
                }}
            />
        <Text 
        style={{ 
            marginLeft: SIZES.base,
            color: COLORS.primary,
            alignSelf:'center',
            justifyContent:'center',
            fontSize:15,
            fontWeight:'bold',
            marginTop:6 
        }}>{item.name}</Text>
        </TouchableOpacity>
    )
   
    const renderCategories=()=>{
        return <View style={{ paddingHorizontal: SIZES.padding }}>
            <View style={{flexDirection:'row',alignItems:'center',justifyContent:'center',paddingBottom:10}}>
            <FontAwesome5 name="book" size={26} color={COLORS.yellow} />
            <Text style={{
                fontWeight:'bold',
                fontSize:20,
                marginLeft:5,
                color:COLORS.black
                }}>Study Categories</Text>
            </View>
            <Animated.View style={{ height: categoryListHeightAnimationValue }}>
                <FlatList
                    data={categories}
                    renderItem={renderItem}
                    keyExtractor={item => `${item.id}`}
                    numColumns={2}
                    showsVerticalScrollIndicator={false}
                />
            </Animated.View>

            <TouchableOpacity
                style={{
                    flexDirection: 'row',
                    marginVertical: SIZES.base,
                    justifyContent: 'center'
                }}
                onPress={() => {
                    if (showMoreToggle) {
                        Animated.timing(categoryListHeightAnimationValue, {
                            toValue: 200,
                            duration: 500,
                            useNativeDriver: false
                        }).start()
                    } else {
                        Animated.timing(categoryListHeightAnimationValue, {
                            toValue: 310,
                            duration: 500,
                            useNativeDriver: false
                        }).start()
                    }

                    setShowMoreToggle(!showMoreToggle)
                }}
            >   
                <View style={{flexDirection:'column',alignItems:'center'}}>
                <Text>{showMoreToggle ? "LESS" : "MORE"}</Text>
                {showMoreToggle ? <AntDesign name="arrowup" size={18} color="black" /> : <AntDesign name="arrowdown" size={18} color="black" />}
                </View>
            </TouchableOpacity>
        </View>
   }


    return(
        <SafeAreaView style={styles.container}>
        {renderHeader()}
        {renderNews()}
        {renderCategories()}
        </SafeAreaView>
    )
}
const styles = StyleSheet.create({
    container:{
        backgroundColor:COLORS.lightGray,
        flex:1
    },
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 2,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 3,
    }
})
export default HomeScreen;
