import React from 'react';
import { NavigationContainer, DefaultTheme } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { createDrawerNavigator } from '@react-navigation/drawer';
//import screen
import HomeScreen from '../screens/HomeScreen';
import StudyTropicScreen from '../screens/StudyTropicScreen';
import jobNewsDetails from '../screens/JobNewsDetails';
import { COLORS } from '../constants';
//import icons
import { BlurView } from 'expo-blur';
//create navigator
const HomeStack = createStackNavigator();
const Drawer = createDrawerNavigator();

const theme = {
    ...DefaultTheme,
    colors: {
      ...DefaultTheme.colors,
      border: 'transparent',
    },
  };
  const HomeStackScreen = ({navigation})=>{
    return(
      <HomeStack.Navigator>
        <HomeStack.Screen 
        name="Home" 
        component={HomeScreen}
        options={{
          headerShown:false,
       }}
         />
      <HomeStack.Screen 
      name="jobDetails" 
      component={jobNewsDetails} 
      options={{
        title:'Circular Details'
      }}
      />
        <HomeStack.Screen 
        name="StudyTropics"  
        component={StudyTropicScreen}
        options={({route})=>({
          title: route.params.title,
          headerStyle:{
            backgroundColor:COLORS.yellow
          },
          headerBackground: () => (
            <BlurView tint="light" intensity={100} style={StyleSheet.absoluteFill} />
          )
        })
      }
        />
      </HomeStack.Navigator>
    )
  }

  
const AppNavigation = ()=>{
    return(
    <NavigationContainer theme={theme}>
      <Drawer.Navigator>
        <Drawer.Screen name="Home" component={HomeStackScreen} />
      </Drawer.Navigator>
        {/* <Stack.Navigator>
        <Stack.Screen name="Home" component={HomeScreen} />
        <Stack.Screen name="BCS"  component={BcsScreen} />
        </Stack.Navigator> */}
    </NavigationContainer>
    )
        
    
}

export default AppNavigation;
