import React,{ useState, useRef } from 'react';
import{
    Image, 
    SafeAreaView, 
    Text, 
    View,
    ScrollView,
    TouchableOpacity,
    FlatList,
    Animated,
    StyleSheet
} from 'react-native';

import {icons,COLORS,SIZES} from '../constants';
import {categories} from '../localdata/data';
import { AntDesign } from '@expo/vector-icons';

const Categories = (props) =>{

    const setSelectedCategory=(id)=>{
        props.navigation.navigate('BCS');
    }


    const categoryListHeightAnimationValue = useRef(new Animated.Value(200)).current;
    const [showMoreToggle, setShowMoreToggle] = useState(false)
    const renderItem = ({ item }) => (
        <TouchableOpacity
            onPress={() =>setSelectedCategory(item.id)}
            style={{
                flex: 1,
                flexDirection: 'column',
                margin: 5,
                paddingVertical: SIZES.radius,
                paddingHorizontal: SIZES.padding,
                borderRadius: 5,
                backgroundColor: COLORS.white,
                justifyContent:'center',
                alignItems:'center',
                ...styles.shadow
            }}
        >
            <Image
                source={item.icon}
                style={{
                    width: 40,
                    height: 40
                }}
            />
            <Text style={{ marginLeft: SIZES.base, color: COLORS.primary,alignSelf:'center',
        justifyContent:'center',fontSize:15,fontWeight:'bold',
        marginTop:6 }}>{item.name}</Text>
        </TouchableOpacity>
    )

    return (
        <View style={{ paddingHorizontal: SIZES.padding - 5 }}>
            <Animated.View style={{ height: categoryListHeightAnimationValue }}>
                <FlatList
                    data={categories}
                    renderItem={renderItem}
                    keyExtractor={item => `${item.id}`}
                    numColumns={2}
                    showsVerticalScrollIndicator={false}
                />
            </Animated.View>

            <TouchableOpacity
                style={{
                    flexDirection: 'row',
                    marginVertical: SIZES.base,
                    justifyContent: 'center'
                }}
                onPress={() => {
                    if (showMoreToggle) {
                        Animated.timing(categoryListHeightAnimationValue, {
                            toValue: 200,
                            duration: 500,
                            useNativeDriver: false
                        }).start()
                    } else {
                        Animated.timing(categoryListHeightAnimationValue, {
                            toValue: 310,
                            duration: 500,
                            useNativeDriver: false
                        }).start()
                    }

                    setShowMoreToggle(!showMoreToggle)
                }}
            >   
                <View style={{flexDirection:'column',alignItems:'center'}}>
                <Text>{showMoreToggle ? "LESS" : "MORE"}</Text>
                {showMoreToggle ? <AntDesign name="arrowup" size={18} color="black" /> : <AntDesign name="arrowdown" size={18} color="black" />}
                </View>
            </TouchableOpacity>
        </View>
    )
}
const styles = StyleSheet.create({
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 2,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 3,
    }
})
export default Categories;
