import React,{useState,useEffect} from 'react';
import{
    Text,
    View,
    Alert,
    StyleSheet,
    FlatList,
    ActivityIndicator
} from 'react-native';
import { TouchableOpacity } from 'react-native-gesture-handler';
import tropicApi from '../api/baseApi';
import {bankStudyTropics} from '../localdata/data';
import { COLORS } from '../constants';

const StudyTropicScreen = ({route,navigation})=>{
    const id = route.params.id;
    const [tropics,setTropics] = useState([]);
    const [isLoading,setIsloading] = useState(true);


    const loadTropics = async()=>{
        if(id==1){
            const {data} = await tropicApi.get('/get-bcs-tropics');
            setTropics(data);
            setIsloading(false);
        }else if(id==2){
            navigation.push('BankTropics');
            
        }else if(id==3){
            setTropics(bankStudyTropics);
            
        }else if(id==4){
            Alert.alert('comming soon....')
            
        }else if(id==5){
            Alert.alert('comming soon....')
           
        }else if(id==6){
            Alert.alert('comming soon....')
            
        }
    }

    const renderItem = ({item})=>{
        return(
            <TouchableOpacity onPress={()=>navigation.push('question',{
                tropicsId:item._id,
                title:item.title
            })}>
            <View style={{
                flex: 1,
                flexDirection: 'column',
                margin: 5,
                paddingVertical: 15,
                paddingHorizontal: 18,
                borderRadius: 5,
                backgroundColor: COLORS.white,
                ...styles.shadow

            }}>
                <Text style={{fontWeight:'bold',fontSize:14,color:COLORS.black}}>{item.title}</Text>
                
            </View>
            </TouchableOpacity>
        )
    }

    useEffect(() => {
        loadTropics()
    }, [0])





    return(
        <View style={styles.container}>
            {isLoading?<ActivityIndicator size="large" color={COLORS.yellow} />:
            <FlatList
            data={tropics}
            keyExtractor={item=>item._id}
            renderItem={renderItem}
            numColumns={1}
            />
            }
        </View>
    )

}

const styles = StyleSheet.create({
    container:{
        flex:1,
        backgroundColor:COLORS.lightGray,
        paddingVertical:5
    },
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 2,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 3,
    }
})

export default StudyTropicScreen;
