import React,{useState,useEffect,useContext} from 'react';
import { 
    View,
    StyleSheet,
    Text,
    FlatList,
    TouchableOpacity,
    ActivityIndicator,
} from 'react-native';
import questionApi from '../api/baseApi';
import {COLORS, SIZES} from '../constants';
import { MaterialIcons } from '@expo/vector-icons';
const ModelTestScreen = ({route})=>{
    const id = route.params.tropicsId
    const [questions,setQuestions] = useState([]);
    const [isLoading,setIsloading] = useState(true);
    const [score,setScore] =  useState(0);
    const [selected,setSelected] = useState([]);
    const [currentQuestion,setCurrentQuestion] = useState(0);


    const loadQuestions = async()=>{
        const {data} = await questionApi.get(`/get-questions/${id}`);
        setQuestions(data);
        setIsloading(false);

    }

    const handleAnswer = (isCorrect)=>{
        if(isCorrect){
            setScore(score+1);
        }
        const nextQuestion = currentQuestion +1;
        if(nextQuestion < questions.length){
            setCurrentQuestion(nextQuestion);
        }else{
            
        }

    }

    useEffect(()=>{
        loadQuestions();
    },[]);

    return(
        <View style={styles.container}>
        <View style={{paddingVertical:6,backgroundColor:COLORS.lightBlue}}>
            
        </View>
        {isLoading?<ActivityIndicator size="large" color={COLORS.blue} />:
            <View style={{
                flex: 1,
                flexDirection: 'column',
                margin: 5,
                paddingVertical: 50,
                paddingHorizontal: 18,
                borderRadius: 5,
                backgroundColor: COLORS.white,
                ...styles.shadow

            }}>
            <Text style={{fontWeight:'bold',fontSize:20,color:COLORS.black}}>{questions[currentQuestion].question}</Text>
                {questions[currentQuestion].options.map((item)=>{
                return(
                  <TouchableOpacity
                    onPress={()=>handleAnswer(item.isCorrect)}
                     style={{
                        flexDirection:'row',
                        paddingVertical:5,
                        alignItems:'center',
                        }}>
                        <View style={{
                            backgroundColor:'gray',
                            width:27,
                            height:27,
                            borderRadius:40,
                            marginRight:6,
                            justifyContent:'center'
                            }}>
                        
                        <Text style={{
                            alignSelf:'center',
                            color:'white',
                        }}>{item.qop}</Text>
                        </View>
                        <Text style={{fontSize:15}}>{item.option}</Text>
                </TouchableOpacity>
                )
                })}
                    
            </View>
            }
        </View>
    )
}
const styles = StyleSheet.create({
    container:{
        flex:1,
        backgroundColor:COLORS.lightGray2,
        paddingVertical:5
    },
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 2,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 3,
    }
})
export default ModelTestScreen;
