import globalContextCreator from '../context/globalContextCreator';
import AsyncStorage from '@react-native-community/async-storage';
const initialState = {
    favoriteId:[]
}

const setToStorage = async(id)=>{
    // const ids = JSON.stringify(initialState.favoriteId[id])
    try{
        await AsyncStorage.setItem('favorites::id',id);
    }catch(err){
        console.log(err);
    }
}

const favoriteReducer = (state,actions)=>{
    switch(actions.type){
        case 'SET_FAVORITE':
            setToStorage(actions.payload);
            return{
                ...state,
                favoriteId:[...state.favoriteId,actions.payload],
            }
        case 'GET_FAVORITE':
            return{
                ...state,
                favoriteId: actions.payload

            }
        default:
            return state;

    }
}

const setToFavorite =  (dispatch) =>{
    return async (id)=>{
     dispatch({
         type:'SET_FAVORITE',
         payload:id
        });
    };
 }
 export const {Context,Provider} = globalContextCreator(favoriteReducer,{setToFavorite},initialState);
