import React,{useState,useEffect} from 'react';
import { 
    View,
    StyleSheet,
    Text,
    FlatList,
    TouchableOpacity,
    ActivityIndicator
} from 'react-native';
import questionApi from '../api/baseApi';
import {COLORS} from '../constants';
const QuestionScreen = ({route})=>{
    const id = route.params.tropicsId
    const [questions,setQuestions] = useState([]);
    const [isLoading,setIsloading] = useState(true);

    const loadQuestions = async()=>{
        const {data} = await questionApi.get(`/get-questions/${id}`);
        setQuestions(data);
        setIsloading(false);

    }
    useEffect(()=>{
        loadQuestions();
    },[0]);


    const renderItem = ({item})=>{
        return(
            <View style={{
                flex: 1,
                flexDirection: 'column',
                margin: 5,
                paddingVertical: 15,
                paddingHorizontal: 18,
                borderRadius: 5,
                backgroundColor: COLORS.white,
                ...styles.shadow

            }}>
                <Text style={{fontWeight:'bold',fontSize:14,color:COLORS.black}}>{item.question}</Text>
            <View>
                <FlatList 
                data={item.options}
                keyExtractor={item=>item._id}
                renderItem={({item})=>{
                    return(
                        <View>
                            <Text>{item.qop}. {item.option}</Text>
                        </View>
                    )
                }}
                />
                <View style={{paddingVertical:7}}>
                <TouchableOpacity>
                    <Text style={{backgroundColor:COLORS.darkgreen}}>See Answer</Text>
                </TouchableOpacity>
                <Text>Answer: {item.answer}</Text>
                </View>
            </View>
            </View>
        )
    }



    return(
        <View style={styles.container}>
            {isLoading?<ActivityIndicator size="large" color={COLORS.yellow} />:
            <FlatList
            data={questions}
            keyExtractor={item=>item._id}
            renderItem={renderItem}
            numColumns={1}
            />
            }
        </View>
    )
}
const styles = StyleSheet.create({
    container:{
        flex:1,
        backgroundColor:COLORS.lightGray2,
        paddingVertical:5
    },
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 2,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 3,
    }
})
export default QuestionScreen;
