import React,{ useState, useRef } from 'react';
import 
{   
    Image, 
    SafeAreaView, 
    Text, 
    View,
    TouchableOpacity,
    FlatList,
    Animated,
    StyleSheet 
} from 'react-native';
//import components
import Header from '../components/Header';
//import icons
import { Ionicons } from '@expo/vector-icons';
import { Feather } from '@expo/vector-icons';

import {icons,COLORS,SIZES} from '../constants';
import {categories,jobNews} from '../localdata/data';
import { AntDesign } from '@expo/vector-icons';


const HomeScreen = ({ navigation }) =>{


    const categoryListHeightAnimationValue = useRef(new Animated.Value(200)).current;
    const [showMoreToggle, setShowMoreToggle] = useState(false)


    //render header component

    const renderHeader=()=>{
        return(
            <View style={{
                flexDirection:'row',
                justifyContent:'space-between',
                paddingHorizontal:8,
                paddingVertical:25,
                alignItems:'center',
                

                }}>
                <View style={{alignSelf:'center',justifyContent:'center'}}>
                <Ionicons 
                style={{paddingLeft:7,alignSelf:'center'}}
                name="ios-menu"
                 size={40}
                 color={COLORS.yellow}
                  onPress={()=>{navigation.openDrawer()}}
                   />
                </View>
                <View>
                <Image 
                source={icons.biome}
                style={{
                    width:145,
                    height:65,
                    resizeMode:'contain',
                    alignSelf:'center'
                }}
                />
                </View>
                
                <View>
                <Feather name="more-vertical" size={24} color={COLORS.yellow} />
                </View>
            </View>
        )
    }

    //render News component
    const renderNews = ()=>{
        return(
        <View style={{paddingHorizontal:15,paddingBottom:15}}>
            <FlatList
            data={jobNews}
            keyExtractor={item=>`${item.id}`}
            renderItem={({item})=>{
                return(
                <TouchableOpacity
                style={{
                    flex: 1,
                    flexDirection: 'column',
                    margin: 5,
                    paddingVertical: SIZES.radius,
                    paddingHorizontal: SIZES.padding,
                    borderRadius: 5,
                    backgroundColor: COLORS.white,
                    ...styles.shadow
                }}
            >
                <View>
                    <Text>{item.title}</Text>
                </View>
                <View>
                    <Text>{item.company}</Text>
                </View>
            </TouchableOpacity>
            )
            }}
            horizontal
            showsHorizontalScrollIndicator={false}
            />
        </View>
        )
    }
   
    const renderItem = ({ item }) => (
        <TouchableOpacity
            onPress={() =>navigation.push('BCS')}
            style={{
                flex: 1,
                flexDirection: 'column',
                margin: 5,
                paddingVertical: SIZES.radius,
                paddingHorizontal: SIZES.padding,
                borderRadius: 5,
                backgroundColor: COLORS.yellow,
                justifyContent:'center',
                alignItems:'center',
                ...styles.shadow
            }}
        >
            <Image
                source={item.icon}
                style={{
                    width: 40,
                    height: 40
                }}
            />
        <Text 
        style={{ 
            marginLeft: SIZES.base,
            color: COLORS.primary,
            alignSelf:'center',
            justifyContent:'center',
            fontSize:15,
            fontWeight:'bold',
            marginTop:6 
        }}>{item.name}</Text>
        </TouchableOpacity>
    )
   
    const renderCategories=()=>{
        return <View style={{ paddingHorizontal: SIZES.padding }}>
            <Animated.View style={{ height: categoryListHeightAnimationValue }}>
                <FlatList
                    data={categories}
                    renderItem={renderItem}
                    keyExtractor={item => `${item.id}`}
                    numColumns={2}
                    showsVerticalScrollIndicator={false}
                />
            </Animated.View>

            <TouchableOpacity
                style={{
                    flexDirection: 'row',
                    marginVertical: SIZES.base,
                    justifyContent: 'center'
                }}
                onPress={() => {
                    if (showMoreToggle) {
                        Animated.timing(categoryListHeightAnimationValue, {
                            toValue: 200,
                            duration: 500,
                            useNativeDriver: false
                        }).start()
                    } else {
                        Animated.timing(categoryListHeightAnimationValue, {
                            toValue: 310,
                            duration: 500,
                            useNativeDriver: false
                        }).start()
                    }

                    setShowMoreToggle(!showMoreToggle)
                }}
            >   
                <View style={{flexDirection:'column',alignItems:'center'}}>
                <Text>{showMoreToggle ? "LESS" : "MORE"}</Text>
                {showMoreToggle ? <AntDesign name="arrowup" size={18} color="black" /> : <AntDesign name="arrowdown" size={18} color="black" />}
                </View>
            </TouchableOpacity>
        </View>
   }


    return(
        <SafeAreaView style={styles.container}>
        {renderHeader()}
        {renderNews()}
        {renderCategories()}
        </SafeAreaView>
    )
}
const styles = StyleSheet.create({
    container:{
        backgroundColor:COLORS.lightGray,
        flex:1
    },
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 2,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 3,
    }
})
export default HomeScreen;
