import React,{useState,useEffect} from 'react';
import{
    Text,
    View,
    Alert,
    StyleSheet,
    FlatList,
    ActivityIndicator
} from 'react-native';
import tropicApi from '../api/baseApi';
import { COLORS } from '../constants';

const StudyTropicScreen = ({route,navigation})=>{
    const id = route.params.id;
    const [tropics,setTropics] = useState([]);
    const [isLoading,setIsloading] = useState(true);








    const loadTropics = async()=>{
        if(id==1){
            const {data} = await tropicApi.get('/get-bcs-tropics');
            setTropics(data);
            setIsloading(false);
        }else if(id==2){
            Alert.alert('comming soon....');
            
        }else if(id==3){
            Alert.alert('comming soon....')
            
        }else if(id==4){
            Alert.alert('comming soon....')
            
        }else if(id==5){
            Alert.alert('comming soon....')
           
        }else if(id==6){
            Alert.alert('comming soon....')
            
        }
    }

    const renderItem = ({item})=>{
        return(
            <View>
                <Text>{item.title}</Text>
            </View>
        )
    }

    useEffect(() => {
        loadTropics()
    }, [0])





    return(
        <View style={styles.container}>
            {isLoading?<ActivityIndicator size="large" color={COLORS.yellow} />:
            <FlatList
            data={tropics}
            keyExtractor={item=>item._id}
            renderItem={renderItem}
            numColumns={1}
            />
            }
        </View>
    )

}

const styles = StyleSheet.create({
    container:{
        flex:1,
        backgroundColor:COLORS.lightGray2,
        paddingVertical:5
    },
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 2,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 3,
    }
})

export default StudyTropicScreen;
