import React,{useState,useEffect} from 'react';
import{
Text,
View,
StyleSheet,
ActivityIndicator
} from 'react-native';
import {COLORS,SIZES} from '../constants';
import jobNewsApi from '../api/jobnews';
import { FlatList } from 'react-native-gesture-handler';
import HTML from "react-native-render-html";
import { format } from "date-fns";
const jobNewsDetails = ({route,navigation})=>{

  const {id} = route.params;
  const [loading,setLoading] = useState(true);
  const [singleCircular,setSingleCircular] = useState({});






    const loadSingleCircular = async ()=>{
        const {data} = await jobNewsApi.get(`/get-single-circular/${id}`);
        setSingleCircular(data);
        setLoading(false);
    }
    useEffect(() => {
      loadSingleCircular();
    }, [0])


    const renderItem = ({item})=>{

        return(
            <View style={{paddingVertical:15}}>
                <View style={{paddingBottom:SIZES.height/2}}>
                <Text style={{fontWeight:'bold',fontSize:15}}>Vacancy : {item.vacancy}</Text>
                <View>
                <Text style={{fontWeight:'bold',fontSize:15}}>Responsibilities : </Text>
                <HTML 
                source={{ html: item.responsibilities }} 
                contentWidth={SIZES.width} 
                />
                </View>
                <View style={{}}>
                <Text style={{fontWeight:'bold',fontSize:16}}>Requirement : </Text>
                <HTML 
                source={{ html: item.requirement }} 
                contentWidth={SIZES.width}
                 />
                </View>
                <Text style={{fontWeight:'bold',fontSize:16}}>Employmentstatus : {item.employmentstatus}</Text>
                <Text style={{fontWeight:'bold',fontSize:16}}>Location : {item.location}</Text>
                <Text style={{fontWeight:'bold',fontSize:16}}>Salary : {item.salary?item.salary:'negotiable'}</Text>
                </View>
            </View>

        )
    }







    return(
        <View style={styles.container}>
            {loading?<ActivityIndicator 
            style={{justifyContent:'center',alignSelf:'center',flex:1}} 
            size="large" color={COLORS.yellow} 
            /> : <View style={{paddingHorizontal:10,paddingVertical:10}}>
                <View>
                    <Text style={{fontSize:28,fontWeight:'bold',color:COLORS.darkgreen}}>{singleCircular.title}</Text>
                    <Text style={{fontSize:14,fontWeight:'bold',color:COLORS.gray}}>Company : {singleCircular.company}</Text>
                    <Text style={{fontSize:14,fontWeight:'bold',color:COLORS.peach}}>Deadline : {format(new Date(singleCircular.deadline), "MMMM do, yyyy")}</Text>
                    <FlatList 
                    data={singleCircular.details}
                    keyExtractor={item=>item._id}
                    renderItem={renderItem}
                    />
                </View>
            </View>}
        </View>
    )

}

const styles = StyleSheet.create({
    container:{
        flex:1,
        backgroundColor:COLORS.lightGray,
    }
})

export default jobNewsDetails;
