import React,{useState,useEffect} from 'react'
import {
    View,
    Text,
    FlatList,
    StyleSheet,
    ActivityIndicator
} from 'react-native'

import circularApi from '../api/baseApi';

const AllCircularScreen = ({route,navigation})=>{

    const [isLoading,setIsLoading] = useState(true);
    const [circular,setCircular] = useState([]);

    const loadCircular = async()=>{
        const {data} = await circularApi.get('/get-all-circulars');
        setCircular(data);
        setIsLoading(false);
    }

    useEffect(()=>{
        loadCircular();
    },[0])



    return(
        <View style={{paddingHorizontal:8,paddingBottom:15}}>

            {isLoading?<ActivityIndicator size="large" color={COLORS.blue} /> :
            <FlatList
            data={circular}
            keyExtractor={item=>item._id}
            renderItem={({item})=>{
                return(
                <TouchableOpacity
                onPress={()=> navigation.push('jobDetails',{
                    id:item._id
                })}
                style={{
                    flex: 1,
                    flexDirection: 'column',
                    margin: 5,
                    paddingVertical: SIZES.radius,
                    paddingHorizontal: 18,
                    borderRadius: 5,
                    backgroundColor: COLORS.white,
                    ...styles.shadow
                }}
            >
                <View>
                    <Text
                    style={{fontWeight:'bold',fontSize:18,color:COLORS.darkgreen}}
                    >{item.title}</Text>
                </View>
                <View style={{flexDirection:'row',alignItems:'center'}}>
                    <Text style={{
                        color:COLORS.blue,
                        fontSize:15,
                        fontWeight:'600'
                    }}>Company : {item.company}</Text>
                    <AntDesign style={{marginLeft:5}} name="doubleright" size={13} color={COLORS.blue} />
                    <Text style={{fontSize:13,color:COLORS.yellow}}>Details</Text>
                </View>
                <View style={{flexDirection:'row',justifyContent:'space-between'}}>
                    <Text style={{fontSize:12,color:COLORS.gray}}>Publish: {format(new Date(item.publish), "MMMM do, yyyy")}</Text>
                    
                </View>
            </TouchableOpacity>
            )
            }}
            horizontal
            showsHorizontalScrollIndicator={false}
            />}
        <View style={{flexDirection:'row',justifyContent:'flex-end',alignItems:'center'}}>
            <TouchableOpacity onPress={()=>navigation.push('allCircular')}>
            <Text style={{fontWeight:'bold',color:COLORS.darkgreen}}>See all</Text>
            </TouchableOpacity>
            <AntDesign style={{marginLeft:5}} name="doubleright" size={12} color={COLORS.darkgreen} />
        </View>
        </View>
    )
}

const styles = StyleSheet.create({
    shadow: {
        shadowColor: "#000",
        shadowOffset: {
            width: 2,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 3,
    }
});

export default AllCircularScreen;
