import React,{useState,useEffect} from 'react';
import{
    Text,
    View,
    Alert
} from 'react-native';
import tropicApi from '../api/baseApi';

const StudyTropicScreen = ({route,navigation})=>{
    const id = route.params.id;
    const [tropics,setTropics] = useState([]);
    const [isLoading,setIsloading] = useState(true);

    const loadTropics = async()=>{
        if(id==1){
            const {data} = await tropicApi.get('/get-bcs-tropics');
            setTropics(data);
            setIsloading(false);
        }else if(id==2){
            Alert.alert('comming soon....');
            navigation.back()
        }else if(id==3){
            Alert.alert('comming soon....')
        }else if(id==4){
            Alert.alert('comming soon....')
        }else if(id==5){
            Alert.alert('comming soon....')
        }else if(id==6){
            Alert.alert('comming soon....')
        }
    }

    useEffect(() => {
        loadTropics()
    }, [0])

    return(
        <View>
            <Text>{route.params.title}{route.params.id}</Text>
        </View>
    )

}

export default StudyTropicScreen;
